import { useState, useRef } from "react";
import styled from "styled-components";
import { mobile } from "../../Utils/responsive";
import { useDispatch } from "react-redux";
import { authActions } from "../../Redux/authRedux";
import { publicRequest } from "../../Utils/requestMethods";
import { toast } from "react-toastify";
import { carMakers } from "../../Data/data";
import { useNavigate } from "react-router-dom";

const AuthComp = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  //ref to get the input form values
  const emailInputRef = useRef();
  const passwordInputRef1 = useRef();
  const passwordInputRef2 = useRef();
  const nameInputRef = useRef();
  const surInputRef = useRef();
  const numberInputRef = useRef();
  const makerInputRef = useRef();
  const licenseInputRef = useRef();
  const typeInputRef = useRef();
  let enteredEmail = "";
  let enteredPassword = "";
  let enteredFirstName = "";
  let enteredSurname = "";
  let enteredMobilePhone = "";
  let license = "";
  let maker = "";
  let type = "";

  //to change password input classes
  const [invalidPassword, setInvalidPassword] = useState({
    border: "1px solid white",
    backgroundColor: "#f1e1fc",
  });

  const [isLogin, setIsLogin] = useState(true);

  // This variable determines whether password is shown or not
  const [isShown, setIsSHown] = useState(false);

  // This function is called when the checkbox is checked or unchecked
  const togglePassword = () => {
    setIsSHown((isShown) => !isShown);
  };

  //changes if I'm on the login form or sign up
  const switchAuthModeHandler = () => {
    setIsLogin((isLogin) => !isLogin);
  };

  //changes the password input class
  const clearWrongPassword = () => {
    setInvalidPassword({
      border: "1px solid white",
      backgroundColor: "#f1e1fc",
    });
  };

  const passwordIsEqual = (event) => {
    event.preventDefault();

    //if I'm on login page I get the values to login
    if (isLogin) {
      enteredEmail = emailInputRef.current.value;
      enteredPassword = passwordInputRef1.current.value;
      submitHandler();
      //if I'm registering a new user I have to compare if the passwords typed are equal.
    } else if (passwordInputRef1.current.value === passwordInputRef2.current.value) {
      enteredEmail = emailInputRef.current.value;
      enteredPassword = passwordInputRef1.current.value;
      submitHandler();
    } else {
      //change the password input class
      toast.error("Passwords do not match!");
      setInvalidPassword({
        border: "1px solid #b40e0e",
        backgroundColor: "#fddddd",
      });
    }
  };

  const submitHandler = async (event) => {
    // event.preventDefault();

    try {
      //if I'm loging in, i don't need to submit/post anithing to our DB only if I'm registering a new user
      //REGISTERING A NEW USER
      if (!isLogin) {
        enteredFirstName = nameInputRef.current.value;
        enteredSurname = surInputRef.current.value;
        enteredMobilePhone = numberInputRef.current.value;
        maker = makerInputRef.current.value;
        type = typeInputRef.current.value;
        license = licenseInputRef.current.value;
        // console.log(maker);
        await publicRequest.post("/auth/register", {
          enteredFirstName,
          enteredSurname,
          enteredMobilePhone,
          enteredEmail,
          enteredPassword,
          maker,
          type,
          license,
        });
          // after registering, navigate to verification page (prefill email)
          // do not auto-login until verification
          navigate(`/verify-otp?email=${encodeURIComponent(enteredEmail)}`);
          return;
      }

      const res = await publicRequest.post("/auth/login", {
        enteredEmail,
        enteredPassword,
      });

      const data = await res.data;
      // If backend requires OTP for login, redirect to verify page with purpose=login
      if (data && data.requiresOtp) {
        navigate(`/verify-otp?email=${encodeURIComponent(enteredEmail)}&purpose=login`);
        return;
      }

      // Otherwise backend returned token and user info; complete login
      dispatch(
        authActions.login({
          token: data.accessToken,
          isAdmin: data.isAdmin,
          userId: data._id,
          maker: data.maker,
          type: data.type,
          license: data.license,
        })
      );
      window.location.reload(false);
    } catch (err) {
      setInvalidPassword({
        border: "1px solid #b40e0e",
        backgroundColor: "#fddddd",
      });
      // If login was blocked due to unverified email, redirect user to verify page
      const status = err?.response?.status;
      if (status === 403) {
        const emailToVerify = enteredEmail || emailInputRef.current?.value;
        if (emailToVerify) {
          navigate(`/verify-otp?email=${encodeURIComponent(emailToVerify)}`);
          return;
        }
      }
      toast.error("Credentials not valid!");
    }
  };

  return (
    <ComponentAuth>
      <Auth>
        <AuthH1>{isLogin ? "Sign In" : "Sign Up"}</AuthH1>
        {/* <form> */}
        <form onSubmit={passwordIsEqual}>
          {!isLogin && (
            <div>
              <Control>
                <ControlLabel htmlFor="firstName">First Name</ControlLabel>
                <ControlInput
                  type="text"
                  id="firstName"
                  placeholder="First Name..."
                  required
                  ref={nameInputRef}
                />
              </Control>
              <Control>
                <ControlLabel htmlFor="surName">Surname</ControlLabel>
                <ControlInput
                  type="text"
                  id="surName"
                  placeholder="Last Name..."
                  required
                  ref={surInputRef}
                />
              </Control>
              <Control>
                <ControlLabel htmlFor="phone">Mobile Number</ControlLabel>
                <ControlInput
                  type="text"
                  id="phone"
                  placeholder="Phone Number with no space..."
                  required
                  ref={numberInputRef}
                />
              </Control>
              <Control>
                <ControlLabel htmlFor="carType">Car Type</ControlLabel>
                <SelectInput id="carType" required ref={typeInputRef}>
                  <option value="">Select your car type...</option>
                  <option value="Motorbike">Motorbike</option>
                  <option value="Car">Car</option>
                  <option value="Small Van">Small Van</option>
                  <option value="Small Bus">Small Bus</option>
                </SelectInput>
              </Control>
              <Control>
                <ControlLabel htmlFor="maker">Car Maker</ControlLabel>
                <SelectInput id="maker" required ref={makerInputRef}>
                  <option value="">Select your car maker...</option>
                  {carMakers.map((carMaker) => {
                    return (
                      <option key={carMaker.id} value={carMaker.title}>
                        {carMaker.title}
                      </option>
                    );
                  })}
                  <option value="Other">Other</option>
                </SelectInput>
              </Control>
              <Control>
                <ControlLabel htmlFor="license">License Plate</ControlLabel>
                <ControlInput
                  type="text"
                  id="license"
                  placeholder="License plate..."
                  required
                  ref={licenseInputRef}
                />
              </Control>
            </div>
          )}
          <Control>
            <ControlLabel htmlFor="email">Your Email</ControlLabel>
            <ControlInput
              type="email"
              id="email"
              placeholder="Email..."
              required
              ref={emailInputRef}
            />
          </Control>
          <Control>
            <ControlLabel htmlFor="password">Your Password</ControlLabel>
            <EqualPassword invalidPassword={invalidPassword}>
              <ControlInput
                type={isShown ? "text" : "password"}
                id="password"
                ref={passwordInputRef1}
                onChange={clearWrongPassword}
                placeholder="Password..."
                required
              />
              {!isLogin && (
                <ControlInput
                  type={isShown ? "text" : "password"}
                  id="password"
                  ref={passwordInputRef2}
                  onChange={clearWrongPassword}
                  placeholder="Password..."
                  required
                />
              )}
            </EqualPassword>
            <h4>Show password?</h4>
            <CheckboxContainerInput
              id="checkbox"
              type="checkbox"
              checked={isShown}
              onChange={togglePassword}
            />
          </Control>
          <Actions>
            <ActionsButton>{isLogin ? "Login" : "Create Account"}</ActionsButton>
            {isLogin && (
              <ForgotButton
                type="button"
                onClick={() =>
                  navigate(
                    `/password-reset?email=${encodeURIComponent(
                      emailInputRef.current?.value || ""
                    )}`
                  )
                }
              >
                Forgot password?
              </ForgotButton>
            )}
            <ActionsToggle type="button" onClick={switchAuthModeHandler}>
              {isLogin ? "Create new account" : "Login with existing account"}
            </ActionsToggle>
          </Actions>
        </form>
      </Auth>
    </ComponentAuth>
  );
};

