import React, { useState } from "react";
import styled from "styled-components";
import { publicRequest } from "../../Utils/requestMethods";
import { toast } from "react-toastify";
import { useDispatch } from "react-redux";
import { authActions } from "../../Redux/authRedux";
import { useNavigate } from "react-router-dom";

const VerifyOtp = ({ email, onVerified, purpose = "register" }) => {
  const [otp, setOtp] = useState("");
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await publicRequest.post("/auth/verify-otp", {
        email,
        otp,
        purpose,
      });

      toast.success("Verified");

      // If login token received
      if (res?.data?.accessToken) {
        const data = res.data;
        dispatch(
          authActions.login({
            token: data.accessToken,
            isAdmin: data.isAdmin,
            userId: data._id,
            maker: data.maker,
            type: data.type,
            license: data.license,
          })
        );
        navigate("/");
        return;
      }

      if (onVerified) onVerified();
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to verify OTP");
    } finally {
      setLoading(false);
    }
  };

  return (
    <CardContainer>
      <Title>Verify OTP</Title>
      <SubText>Enter the 6-digit code sent to <strong>{email}</strong></SubText>

      <Form onSubmit={submit}>
        <Input
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          required
          maxLength={6}
          placeholder="Enter OTP"
        />

        <Button disabled={loading}>
          {loading ? "Verifying..." : "Verify OTP"}
        </Button>
      </Form>
    </CardContainer>
  );
};

export default VerifyOtp;

// -------------------------- Styled Components --------------------------

const CardContainer = styled.div`
  width: 100%;
  max-width: 380px;
  margin: 2rem auto;
  padding: 2rem;
  background: white;
  border-radius: 16px;
  box-shadow: 0px 8px 25px rgba(0, 0, 0, 0.08);
  display: flex;
  flex-direction: column;
  gap: 1.2rem;
`;

const Title = styled.h2`
  text-align: center;
  margin: 0;
  font-weight: 600;
  color: #222;
`;

const SubText = styled.p`
  text-align: center;
  margin: 0;
  color: #666;
  font-size: 0.95rem;
`;

const Form = styled.form`
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Input = styled.input`
  padding: 0.9rem;
  font-size: 1rem;
  border-radius: 10px;
  border: 1px solid #dcdcdc;
  outline: none;
  letter-spacing: 2px;
  text-align: center;

  &:focus {
    border-color: #4f46e5;
    box-shadow: 0 0 0 2px rgba(79, 70, 229, 0.2);
  }
`;

const Button = styled.button`
  padding: 0.9rem;
  border: none;
  border-radius: 10px;
  background: #4f46e5;
  color: white;
  font-size: 1rem;
  cursor: pointer;
  transition: 0.2s;

  &:hover {
    background: #4338ca;
  }

  &:disabled {
    background: #a5a5a5;
    cursor: not-allowed;
  }
`;
