import React, { useState } from "react";
import styled from "styled-components";
import { publicRequest } from "../../Utils/requestMethods";
import { toast } from "react-toastify";
import { useDispatch } from "react-redux";
import { authActions } from "../../Redux/authRedux";
import { useNavigate } from "react-router-dom";

const VerifyOtp = ({ email, onVerified, purpose = "register" }) => {
  const [otp, setOtp] = useState("");
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await publicRequest.post("/auth/verify-otp", { email, otp, purpose });
      toast.success("Verified");

      // If server returned an accessToken (purpose=login), use it to complete login
      if (res?.data?.accessToken) {
        const data = res.data;
        dispatch(
          authActions.login({
            token: data.accessToken,
            isAdmin: data.isAdmin,
            userId: data._id,
            maker: data.maker,
            type: data.type,
            license: data.license,
          })
        );
        // navigate to home after login
        navigate("/");
        return;
      }

      // otherwise call onVerified for registration flow
      if (onVerified) onVerified();
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to verify OTP");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Form onSubmit={submit}>
      <Label>Enter code sent to {email}</Label>
      <Input value={otp} onChange={(e) => setOtp(e.target.value)} required />
      <Button disabled={loading}>{loading ? "Verifying..." : "Verify"}</Button>
    </Form>
  );
};

export default VerifyOtp;

const Form = styled.form``;
const Label = styled.div`margin-bottom: 0.5rem;`;
const Input = styled.input`padding: 0.6rem; border-radius: 6px; width: 100%;`;
const Button = styled.button`margin-top: 0.6rem; padding: 0.6rem 1rem;`;
