import React, { useState } from "react";
import styled from "styled-components";
import { publicRequest } from "./Utils/requestMethods";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const PasswordReset = () => {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const submitHandler = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await publicRequest.post("/auth/request-password-reset", { email });
      toast.success("Password reset token sent to your email!");

      navigate(`/password-reset?email=${encodeURIComponent(email)}`);
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to send OTP");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Wrapper>
      <Card>
        <Title>Forgot Password</Title>
        <SubText>Enter your registered email to receive OTP.</SubText>

        <Form onSubmit={submitHandler}>
          <Input
            type="email"
            required
            value={email}
            placeholder="Enter your email"
            onChange={(e) => setEmail(e.target.value)}
          />

          <Button disabled={loading}>
            {loading ? "Sending..." : "Send OTP"}
          </Button>
        </Form>

        <SmallText>
          Remember password?{" "}
          <Highlight onClick={() => navigate("/auth")}>Login</Highlight>
        </SmallText>
      </Card>
    </Wrapper>
  );
};

export default PasswordReset;

/* ---------------------- UI STYLING (ONLY UI CHANGED) ---------------------- */

const Wrapper = styled.div`
  width: 100%;
  min-height: 80vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1rem;
  background: #f7f7f7;
`;

const Card = styled.div`
  width: 100%;
  max-width: 380px;
  background: white;
  padding: 2rem;
  border-radius: 16px;
  box-shadow: 0px 8px 25px rgba(0, 0, 0, 0.08);
`;

const Title = styled.h2`
  text-align: center;
  margin: 0;
  font-weight: 600;
  color: #222;
`;

const SubText = styled.p`
  text-align: center;
  margin-top: 0.4rem;
  margin-bottom: 1.4rem;
  color: #555;
  font-size: 0.95rem;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Input = styled.input`
  padding: 0.9rem;
  border-radius: 10px;
  border: 1px solid #d0d0d0;
  font-size: 1rem;
  outline: none;
  width: 100%;

  &:focus {
    border-color: #4f46e5;
    box-shadow: 0 0 0 2px rgba(79, 70, 229, 0.15);
  }
`;

const Button = styled.button`
  padding: 0.9rem;
  border-radius: 10px;
  border: none;
  background: #4f46e5;
  color: white;
  font-size: 1rem;
  cursor: pointer;
  transition: 0.25s;

  &:hover {
    background: #4338ca;
  }

  &:disabled {
    background: #888;
    cursor: not-allowed;
  }
`;

const SmallText = styled.p`
  margin-top: 1rem;
  text-align: center;
  font-size: 0.9rem;
  color: #666;
`;

const Highlight = styled.span`
  color: #4f46e5;
  cursor: pointer;
  font-weight: 600;

  &:hover {
    text-decoration: underline;
  }
`;
