import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import ProductsDetails from "../ProductsDetails";

const OrderDetails = (props) => {
  const { orderId, products, status } = props;
  return (
    <UserDetailsContainer>
      <TopContainer>
        <h3>Status: {status}</h3>
        <h3>SHOPPING INFO</h3>
        <Link to={`/invoice/${orderId}`} state={{ from: props }}>
          <Button>Invoice</Button>
        </Link>
      </TopContainer>
      <OrderID>Order # {orderId}</OrderID>
      {products.map((order, index) => (
        <ProductsDetails key={index} name={order.name} amount={order.amount} />
      ))}
    </UserDetailsContainer>
  );
};

export default OrderDetails;

const UserDetailsContainer = styled.li`
  display: flex;
  flex-direction: column;
  margin: 1rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid black;
  & h3 {
    margin: 0 0 0.25rem 0;
    font-size: 1.4rem;
  }
`;

const TopContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px 15px;
  background-color: black;
  box-shadow: 0 3px 24px rgb(0 0 0 / 30%);
  border-radius: 1rem;
  color: white;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.5s ease;
  &:hover {
    transform: scale(1.1);
  }
`;
const OrderID = styled.div`
  color: rgb(163, 153, 153);
  font-weight: 600;
  padding-bottom: 10px;
  font-size: 0.9rem;
`;
