import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import { publicRequest } from "../Utils/requestMethods";
import { toast } from "react-toastify";

const PasswordReset = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [token, setToken] = useState("");
  const [step, setStep] = useState(1);
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const e = params.get("email");
    const t = params.get("token");
    if (e) setEmail(e);
    if (t) {
      setToken(t);
      setStep(2);
    }
  }, [location.search]);

  const requestOtp = async () => {
    try {
      await publicRequest.post("/auth/request-password-reset", { email });
      toast.info("Password reset token sent to email");
    na
      setStep(2);
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to request OTP");
    }
  };

  const reset = async () => {
    if (newPassword !== confirmPassword) {
      toast.error("Passwords do not match");
      return;
    }

    try {
      const tokenToUse = token || otp;
      await publicRequest.post("/auth/reset-password", {
        email,
        token: tokenToUse,
        newPassword,
      });

      toast.success("Password reset successful");
      setStep(3);
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to reset password");
    }
  };

  return (
    <Wrapper>
      <Card>
        {step === 1 && (
          <>
            <Title>Request Password Reset</Title>

            <Label>Email</Label>
            <Input
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your registered email"
            />

            <Button onClick={requestOtp}>Send Code</Button>
          </>
        )}

        {step === 2 && (
          <>
            <Title>Reset Password</Title>

            
            <Label>New Password</Label>
            <Input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Enter new password"
            />

            <Label>Confirm Password</Label>
            <Input
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Re-enter new password"
            />

            <Button onClick={reset}>Reset Password</Button>
          </>
        )}

        {step === 3 && (
          <SuccessMsg>Password reset complete. Please login.</SuccessMsg>
        )}
      </Card>
    </Wrapper>
  );
};

export default PasswordReset;

/* -------------------------- UI STYLING -------------------------- */

const Wrapper = styled.div`
  width: 100%;
  min-height: 70vh;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding-top: 4rem;
  padding-bottom: 3rem;
`;

const Card = styled.div`
  width: 95%;
  max-width: 420px;
  background: white;
  padding: 2rem;
  border-radius: 14px;
  box-shadow: 0px 6px 20px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h3`
  margin: 0 0 1.2rem 0;
  text-align: center;
  font-weight: 600;
  color: #222;
`;

const Label = styled.label`
  margin: 0.5rem 0;
  display: block;
  color: #444;
  font-size: 0.95rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.85rem;
  border: 1px solid #cccccc;
  border-radius: 8px;
  font-size: 1rem;
  margin-bottom: 1rem;
  outline: none;

  &:focus {
    border-color: #d62828;
    box-shadow: 0 0 0 2px rgba(214, 40, 40, 0.2);
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 0.85rem;
  background: #d62828;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  cursor: pointer;
  transition: 0.25s;

  &:hover {
    background: #b71d1d;
  }
`;

const SuccessMsg = styled.div`
  padding: 1rem;
  text-align: center;
  font-size: 1.1rem;
  color: green;
  font-weight: 600;
`;
