import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import { publicRequest } from "../Utils/requestMethods";
import { toast } from "react-toastify";

const PasswordReset = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [token, setToken] = useState("");
  const [step, setStep] = useState(1);
  const [newPassword, setNewPassword] = useState("");
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const e = params.get("email");
    const t = params.get("token");
    if (e) setEmail(e);
    if (t) {
      setToken(t);
      setStep(2);
    }
  }, [location.search]);

  const requestOtp = async () => {
    try {
      await publicRequest.post("/auth/request-password-reset", { email });
      toast.info("Password reset token sent to email");
      setStep(2);
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to request OTP");
    }
  };

  const reset = async () => {
    try {
      // support token from query or entered in form
      const tokenToUse = token || otp;
      await publicRequest.post("/auth/reset-password", { email, token: tokenToUse, newPassword });
      toast.success("Password reset successful");
      setStep(3);
    } catch (err) {
      toast.error(err?.response?.data?.error || "Failed to reset password");
    }
  };

  return (
    <Container>
      {step === 1 && (
        <div>
          <h3>Request password reset</h3>
          <input value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
          <button onClick={requestOtp}>Send code</button>
        </div>
      )}
      {step === 2 && (
        <div>
          <h3>Enter token and new password</h3>
          <input value={token} onChange={(e) => setToken(e.target.value)} placeholder="Token from email (or token param)" />
          <input value={newPassword} onChange={(e) => setNewPassword(e.target.value)} placeholder="New password" />
          <button onClick={reset}>Reset password</button>
        </div>
      )}
      {step === 3 && <div>Password reset complete. Please login.</div>}
    </Container>
  );
};

export default PasswordReset;

const Container = styled.div`padding: 1rem;`;
